function App() {
  const [desc, setDesc] = useState("");
  const [todoDate, setTodoDate] = useState("");
  const [todos, setTodos] = useState([]);

  const inputChanged = e => {
    setDesc(e.target.value);
  };

  const addTodo = e => {
    e.preventDefault();
    setTodos([...todos, { desc, todoDate }]);
  };
